const Previous = ({props}) => {
    return(
        <button
            name="control"
            className="controlBtn ui-btn-right ui-btn ui-btn-b ui-btn-inline ui-mini ui-corner-all"
            id="controlBtn-prev"
            value="prev"
            onClick={props.goToPrevQuestion}>
            Previous
        </button>
    )
}

const Next = ({props}) => {
    return(
        <button
            name="control"
            className="controlBtn ui-btn-right ui-btn ui-btn-b ui-btn-inline ui-corner-all ui-mini"
            id="controlBtn-next"
            value="next"
            onClick={ () => {
                if(props.correct){
                    props.goToNextQuestion()
                }else { console.log("wrong answer!") }
            }}>
            Next
        </button>
    )
}

const Submit = ({props}) => {
    const handleSubmit = (selected, correct) => {
        //console.log( selected )
        console.log( correct )
        console.log( selected )
        if( selected == correct ){
            props.setCorrect(true)
            console.log("correct")
        } else{
            props.setCorrect(false)
        }

    }

    return(
        <button
            name="control"
            className="controlBtn cf ui-btn-right ui-btn ui-btn-b ui-btn-inline ui-corner-all ui-mini"
            id="controlBtn-submit"
            value="submit"
            onClick={() => {
                handleSubmit(props.selectedAnswer, props.correctAnswer)}
            }>
            Submit
        </button>
    )
}




