import React from "react"
import Buttons from "./Buttons"

//contain question text
const QuestionText = ({props}) => {
    if( props.questionNum != -1 ){ //-1 is start menu
        return (props.isFetched) ? (
            <>
                <p className="question-number">Question {props.questionNum + 1} out of 5{props.Question}</p>
                <p className="question">{props.questionTexts[props.questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
            </>
        ) : (<p>Still Loading..</p>)
    } else { return <></> }
}


//display choices
const ChoiceList = ({props}) => {

    //filter choice for this question
    const choices = props.answers.filter( (_ , index) => {
        return ((props.questionNum*4 <= index) && (index <= props.questionNum*4 + 3))
    })

    var correctAnswer = ""
    //setCorrectAnswer
    choices.forEach( (choice,index) => {
      if( choice.getAttribute("fraction") == 1 ){
          correctAnswer = "answer-"+index
      }  
    })

    var selectedAnswer = ""

    const ChoicesJSX = choices.map( (choice, index) => {
        return(
            <>
                <input
                    type="radio"
                    name={"answer-"+props.questionNum}
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}
                    style={{display:"block"}}>
                Feedback
                </p>
            </>)
    })

    return(
        <form className="mc cf" onChange={ (event) => {
            //setSelectedAnswer
            selectedAnswer = event.target.id
            console.log( correctAnswer )
            console.log( selectedAnswer )
        }}>
        {ChoicesJSX}
        </form>
    )
}

const Choices = ({props}) => {

    if( props.questionNum != -1 ){ //-1 is start menu
        return(
            <ChoiceList props={props}/>
            )
    }else return <></> 
}

class Question extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            correctAnswer:"",
            selectedAnswer:"",
            coorect: false
        }
        this.setCorrectAnswer = this.setCorrectAnswer.bind(this)
        this.setSelectedAnswer = this.setSelectedAnswer.bind(this)
    }

    setCorrectAnswer(answer){
        this.setState({
            correctAnswer: answer
        })
    }

    setSelectedAnswer(){
        this.setState({
            selectedAnswer: answer
        })
    }

    checkAnswer(){

    }

    render(){
        return(
        <>
        <section className="question cf">
            <p className="instruction"></p>
            <QuestionText props={props} />
            <Choices props={props}/>
            <p className="messages"></p>
        </section>

        {console.log(props)}
        <Buttons
          goToNextQuestion={props.goToNextQuestion}
          goToPrevQuestion={props.goToPrevQuestion}/>
        </>
        )
    }
}

export default Question
