import logo from './logo.svg'

import Header from './Header'
import Wrapper from './Wrapper'
import Question from './Question';
import Footer from './Footer';

import React from 'react';

import './App.css';
import XMLData from './xml/mc.xml'
//import './arrangeprotein.css'
//import './bootstrap.min.css'
//import './jquery.mobile-1.4.0.css'
//import './mcstyle.css'
//import './quiz.css'
//import './reset.css'
//import './css/style.css'

class App extends React.Component {

  state = {
    questionText: "",
    questionNum: 0,
    answers:[]
}

  componentDidMount(){
    const questionNum = this.state.questionNum
    axios.get(XMLData,
        {"Content-Type":"application/xml; charset=utf-8"
    }).then((response)=>{
        //there is no need to parse this everytime there is a mount,
        //only need to parse it when the page is loaded
        const parser = new DOMParser()
        var xml = parser.parseFromString(response.data,"text/xml")
        var question = xml.getElementsByTagName("questiontext")[questionNum]
        var answers = xml.getElementsByTagName("answer")
        //var an = answers.slice(questionNum, questionNum+4)
        //console.log( an )
        //.innerHTML <= should I use innerHTML??
        //should I change parse xml as a string or as an JSX object?
        //var answers = xml.getElementsByTagName("")

        //do we have to set every state again??
        this.setState({
            questionText: question.childNodes[0].nodeValue,
            questionNum: questionNum,
            answer1: answers[questionNum*4].childNodes[1].textContent,
            answer2: answers[questionNum*4 +1].childNodes[1].textContent,
            answer3: answers[questionNum*4 +2].childNodes[1].textContent,
            answer4: answers[questionNum*4 +3].childNodes[1].textContent
        })
        console.log(this.state.questionText)

    })
}

  render(){
    return (
      <div className="App">
        <Header/>
        <Wrapper/>
        <Question questionNum={this.state.questionNum}/>
        <Footer />
      </div>
    )
  }
}

export default App;
