import React from "react"


class Question extends React.Component {

    constructor(props){
        this.state = {
            questionNum: 0,
            questionTexts: [],
            answers: []
        }
    }

    Choices = this.props.answers.filter( (_ , index) => {
        return ((this.props.questionNum*4 <= index) && (index <= this.props.questionNum*4 + 3))
    })

    ChoiceDisplay = this.Choices.map( (choice, index) => {
        return(
            <>
                <input
                    type="radio"
                    name=""
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}>
                </p>
            </>
        )
    })

    render(){

        return(
            <section className="question cf">
                <p className="instruction"></p>
                <p className="question-number">{this.props.questionNum+1}</p>
                <p className="question">{this.props.questionTexts[this.props.questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
                <form className="mc cf">
                    {this.ChoiceDisplay}
                </form>
                <p className="messages"></p>
            </section>
        )
    }
}

export default Question
