import React from "react"
import Buttons from "./Buttons"

//contain question text
const QuestionText = ({props}) => {
    if( props.questionNum != -1 ){ //-1 is start menu
        return (props.isFetched) ? (
            <>
                <p className="question-number">Question {props.questionNum + 1} out of 5{props.Question}</p>
                <p className="question">{props.questionTexts[props.questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
            </>
        ) : (<p>Still Loading..</p>)
    } else { return <></> }
}


//display choices
const ChoiceList = ({props}) => {
    console.log(props)


    const ChoicesJSX = props.choices.map( (choice, index) => {
        return(
            <>
                <input
                    type="radio"
                    name={"answer-"+props.questionNum}
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}
                    style={{display:"block"}}>
                Feedback
                </p>
            </>)
    })


    return(
        <form className="mc cf" onChange={(event) => {
            props.setSelectedAnswer(event.target.id)}
            }>
        {ChoicesJSX}
        </form>
    )
}

const Choices = ({props}) => {
    console.log(props)

    if( props.questionNum != -1 ){ //-1 is start menu
        return(
            <ChoiceList props={props}/>
            )
    }else return <></> 
}

class Question extends React.Component{

    createChoices(props){
        return props.answers.filter( (_ , index) => {
            return ((props.questionNum*4 <= index) && (index <= props.questionNum*4 + 3))
        })
    }

    constructor(props){
        super(props)
        this.setCorrectAnswer = this.setCorrectAnswer.bind(this)
        this.setSelectedAnswer = this.setSelectedAnswer.bind(this)

        const choices = this.createChoices(props)

        var correctAnswer 
        choices.forEach( (choice,index) => {
            if( choice.getAttribute("fraction") == 1 ){
                correctAnswer = "answer-"+index
            }  
        })

        this.state = {
            correctAnswer:correctAnswer,
            choices: this.createChoices(),
            selectedAnswer:"",
            correct: false
        }

        console.log( this.state )
    }

    //setCorrectAnswer
    setCorrectAnswer(answer){
        this.setState({
            correctAnswer: answer
        })
    }

    setSelectedAnswer(answer){
        console.log(answer)
        this.setState({
            selectedAnswer: answer
        })
    }

    checkAnswer(){

    }

    render(){
        return(
        <>
        <section className="question cf">
            <p className="instruction"></p>
            <QuestionText props={this.props} />
            <Choices
                props={{...this.props,
                    setSelectedAnswer: this.setSelectedAnswer,
                    setCorrectAnswer: this.setCorrectAnswer,
                    choices: this.state.choices
                }}
            />
            <p className="messages"></p>
        </section>

        {console.log(this.props)}
        <Buttons
          goToNextQuestion={this.props.goToNextQuestion}
          goToPrevQuestion={this.props.goToPrevQuestion}
          checkAnswer={this.checkAnswer}/>

        </>
        )
    }
}

const Debug = (props) =>{
    console.log(props)
    return(<></>)
}

export default Question
