import React from "react"

/*

    quesiton and choice should be updated everytime

*/


class Question extends React.Component {

    constructor(props){
        super(props)

        this.state = {
            Choices: [],
            ChoiceDisplay : [],
            isComputed: false
        }
    }

    render(){
        return(
            <section className="question cf">
                <p className="instruction"></p>
                <QuestionText state={this.state} props={this.props} />
                <form className="mc cf">
                {/* console.log(this.state)*/}
                <Choices />
                </form>
                <p className="messages"></p>
            </section>
        )
    }
}

const QuestionText = ({props, state}) => {
    return (props.isFetched && state.isComputed) ? (
        <>
            <p className="question-number">{props.questionNum}</p>
            <p className="question">{props.questionTexts[props.questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
        </>
    ) : (<p>Still Loading..</p>)
}

const Choices = ({props, state}) => {
    const choices = props.answers.filter( (_ , index) => {
        return ((props.questionNum*4 <= index) && (index <= props.questionNum*4 + 3))
    })

    return(choices.map( (choice, index) => {
        return(<>
                <input
                    type="radio"
                    name=""
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}>
                </p>
            </>)
        })
    )
}

export default Question
