import React from "react"


class Question extends React.Component {

    //if needed to be load => should not be put in constructor??
    //load initial state => 

    constructor(props){
        super(props)

        this.state = {
            Choices: Choices,
            ChoiceDisplay : ChoiceDisplay
        }
    }

    componentDidUpdate(prevProps) {
        if( this.props.isFetched != prevProps.isFetched) { // Check if it's a new user, you can also use some unique property, like the ID  (this.props.user.id !== prevProps.user.id)

            const Choices = this.props.answers.filter( (_ , index) => {
                return ((this.props.questionNum*4 <= index) && (index <= this.props.questionNum*4 + 3))
            })
            console.log( this.props.answers )

            const ChoiceDisplay = Choices.map( (choice, index) => {
                return(
                    <>
                        <input
                            type="radio"
                            name=""
                            id={"answer-"+index}
                            value={choice.childNodes[1].textContent}/>
                        <label
                            htmlFor={"answer-"+index}
                            className="hovereffect">
                        {choice.childNodes[1].textContent}</label>
                        <p
                            className="feedback"
                            id={"feedback-"+index}>
                        </p>
                    </>
                )
            })

            this.setState({
                Choices: Choices,
                ChoiceDisplay : ChoiceDisplay
            })

            console.log( "new state set!" )
        }
    }

    show(){
        return this.props.isFetched ? (
            <>
                <p className="question-number">{this.props.questionNum+1}</p>
                <p className="question">{this.props.questionTexts[this.props.questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
                <form className="mc cf">
                    {console.log(this.state)}
                    {/*this.state.ChoiceDisplay*/}
                </form>
            </>
        ) : (<p>Still Loading..</p>)
    }

    render(){
        return(
            <section className="question cf">
                <p className="instruction"></p>
                {console.log( this.state )}
                {this.show()}
                <p className="messages"></p>
            </section>
        )
    }
}

export default Question
