import Header from './Header'
import Instruction from './Instruction'
import Question from './Question'
import React from 'react'
import axios from 'axios'

//import './App.css';
//path must be changed accordingly to the param query input

/*

BUGG: PRESS SUBMIT AND IT SHOWS THE CORRECT ANSWER

*/

import XMLData from './xml/mc.xml'
//import './arrangeprotein.css'
//import './bootstrap.min.css'
//import './jquery.mobile-1.4.0.css'
import './mcstyle.css'
//import './quiz.css'
//import './style.css'

class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      isFetched: false, //isFetched is reset everytime a new component is updated
      questions: [],
      start: -1,
      questionNum: -1,
      questionTexts: [],
      answers: [],
      selectedAnswer: -1,
      correct: null,
      pressSubmit: 0
    }
  }

  setSubmit = (state) => {
    this.setState({
      pressSubmit: state
    })
  }

  resetSelected = () => {

  }

  goToNextQuestion = () => { 
    this.setState({
      questionNum : this.state.questionNum + 1,
      correct: null,
      selectedAnswer: -1,
      pressSubmit: 0
    })
  }

  goToPrevQuestion = () => { this.setState({
      questionNum : this.state.questionNum - 1, 
      correct: null,
      selectedAnswer: -1,
      pressSubmit: 0
    })
  }

  setCorrect = (state) => { this.setState({
      correct: state })
  }

  setSelectedAnswer = (answer) => { this.setState({
      selectedAnswer: answer,
      pressSubmit: 0
     })
  }

  componentDidMount(){
    this.fetchQuestionsWithAxios()
  }

  fetchQuestionsWithAxios(){
    axios.get(XMLData,
      {"Content-Type":"application/xml; charset=utf-8"
    }).then((response)=>{
      const parser = new DOMParser()
      var xml = parser.parseFromString(response.data,"text/xml")

      var questions = Array.from(xml.getElementsByTagName("question"))

      var answers = Array.from(xml.getElementsByTagName("answer"))
      //answers should be an array of <answer>

      this.setState({
          questions: questions,
          answers: answers,
          //isFetched:true
      })
    }).catch( e => {
      console.log(e)
      //this.setState({ isFetched: false })
    })
  }

  render(){
    //console.log( this.state )
    return (
      <div className="App">
        <Header/>
        <div id ="wrapper" data-role="content">
          <Instruction
            questionNum={this.state.questionNum}
            goToNextQuestion={this.goToNextQuestion}/>
          <Question
            questionNum={this.state.questionNum}
            questions={this.state.questions}
            //isFetched={this.state.isFetched}
            choices = {this.state.answers.filter( (_ , index) => {
              return ((this.state.questionNum*4 <= index) && (index <= this.state.questionNum*4 + 3))
            })}
            goToNextQuestion={this.goToNextQuestion}
            goToPrevQuestion={this.goToPrevQuestion}
            setSelectedAnswer={this.setSelectedAnswer}
            selectedAnswer={this.state.selectedAnswer}
            setCorrect={this.setCorrect}
            correct={this.state.correct}
            pressSubmit={this.state.pressSubmit}
            setSubmit={this.setSubmit}
          />
        </div>
      </div>
    )
  }
}

export default App;
