import React from "react"


class Question extends React.Component {

    Choices = answers.filter( (_ , index) => {
        return ((questionNum*4 <= index) && (index <= questionNum*4 + 3))
    })

    ChoiceDisplay = Choices.map( (choice, index) => {
        return(
            <>
                <input
                    type="radio"
                    name=""
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}>
                </p>
            </>
        )
    })

    render(){

        return(
            <section className="question cf">
                <p className="instruction"></p>
                <p className="question-number">{this.props.questionNum+1}</p>
                <p className="question">{this.props.questionTexts[questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
                <form className="mc cf">
                    {ChoiceDisplay}
                </form>
                <p className="messages"></p>
            </section>
        )
    }
}

export default Question
