import Header from './Header'
import Instruction from './Instruction'
import Question from './Question'
import React from 'react'
import axios from 'axios'

import XMLData from './xml/mc0.xml'
import './mcstyle.css'

/*
Need Updates:
  Number of question display, i.e. Question _ out of _ must be dynamic
  i.e. change accordingly to number of questions in xml file

  RANDOM ANSWER
  choices must be shuffled accordingly to randomanswer parameter in xml file

  <br /> TAG
  <br /> Tag parsing has a problem in displaying questions

  <images/ >
  image tag could not be displayed



*/

class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      questionsList: [],
      questionNum: -1,
      questionTexts: [],
      answersList: [],
      selectedAnswer: -1,
      correct: null,
      pressSubmit: 0
    }
  }

  setSubmit = (state) => {
    this.setState({
      pressSubmit: state
    })
  }

  resetSelected = () => {
    this.setState({
      correct: null,
      selectedAnswer: -1,
      pressSubmit: 0
    })
  }

  goToNextQuestion = () => { 
    this.setState({
      questionNum : this.state.questionNum + 1,
      correct: null,
      selectedAnswer: -1,
      pressSubmit: 0
    })
  }

  goToPrevQuestion = () => { this.setState({
      questionNum : this.state.questionNum - 1, 
      correct: null,
      selectedAnswer: -1,
      pressSubmit: 0
    })
  }

  setCorrect = (state) => { this.setState({
      correct: state })
  }

  setSelectedAnswer = (answer) => { this.setState({
      selectedAnswer: answer,
      pressSubmit: 0
     })
  }

  componentDidMount(){
    this.fetchQuestionsWithAxios()
  }

  fetchQuestionsWithAxios(){
    axios.get(XMLData,
      {"Content-Type":"application/xml; charset=utf-8"
    }).then((response)=>{
      const parser = new DOMParser()
      var xml = parser.parseFromString(response.data,"text/xml")

      var questionsList = Array.from(xml.getElementsByTagName("question"))

      const shuffle = (array) => {
        var currentIndex = array.length,  randomIndex;

        // While there remain elements to shuffle...
        while (currentIndex != 0) {

          // Pick a remaining element...
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex--;

          // And swap it with the current element.
          [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }
        return array;
      }

      var answersList = []
      for( var i = 0 ; i < questionsList.length ; i++ ){
        var answers = Array.from( questionsList[i].getElementsByTagName("answer") )
        if( questionsList[i].getAttribute("randomanswer").localeCompare("true")){
          console.log( answers )
          shuffle( answers )
          console.log( answers )
        }
        answersList.push( answers )
      }

      console.log( answersList )

      this.setState({
          questionsList: questionsList,
          answersList: answersList,
      })
    }).catch( e => {
      console.log(e)
    })
  }

  render(){
    //console.log( this.state )

    /*
            choices = {this.state.answers.filter( (_ , index) => {
              return ((this.state.questionNum*4 <= index) && (index <= this.state.questionNum*4 + 3))
            })}*/

    return (
      <div className="App">
        <Header/>
        <div id ="wrapper" data-role="content">
          <Instruction
            questionNum={this.state.questionNum}
            goToNextQuestion={this.goToNextQuestion}/>
          <Question
            questionNum={this.state.questionNum}
            questionsList={this.state.questionsList}
            choices = {this.state.answersList[this.state.questionNum]}
            selectedAnswer={this.state.selectedAnswer}
            correct={this.state.correct}
            pressSubmit={this.state.pressSubmit}
            state={this.state} //for DEBUGGING

            goToNextQuestion={this.goToNextQuestion}
            goToPrevQuestion={this.goToPrevQuestion}
            setSelectedAnswer={this.setSelectedAnswer}

            setCorrect={this.setCorrect}
            setSubmit={this.setSubmit}
            resetSelected={this.resetSelected}
          />
        </div>
      </div>
    )
  }
}

export default App;
