import React, { useState } from "react"
import Buttons from "./Buttons"

//contain question text
const QuestionText = ({props}) => {
    const breakLine = (text) => {
        var regex = /(<br \/>)/g;
        return text.split(regex).map(function(line, index) {
            return line.match(regex) ? <br key={"key_" + index} /> : line;
        });
    }

    if( props.questionNum != -1 ){ //-1 is start menu
        return (<>
            <p className="question-number">Question {props.questionNum + 1} out of {props.totalQuestions}</p>
            {/* {console.log(props.questionsList[props.questionNum].childNodes[1])} */}
            <p className="question">{breakLine(props.questionsList[props.questionNum].childNodes[1].textContent)}</p>
        </>)

    } else { return <></> }
}

//display choices
const ChoiceList = ({props}) => {
    // console.log( props.choices )

    const ChoicesJSX = props.choices.map( (choice, index) => {
        // console.log(props.selectedAnswer)
        return(
            <>
                <div className="ui-radio">
                <label
                    htmlFor={"answer-"+index}
                    className={"hovereffect ui-btn ui-corner-all ui-btn-inherit ui-btn-icon-left "+((index==props.selectedAnswer)? "ui-radio-on":"ui-radio-off")}>
                {choice.childNodes[1].textContent}
                </label>
                <input
                    type="radio"
                    name={"answer-"+props.questionNum}
                    index={index}
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                </div>
                <p
                    className="feedback"
                    id={"feedback-"+index}
                    style={{display: ( (props.selectedAnswer == index) && (props.correct != null )) ? "block" : "none"}}>
                {choice.childNodes[3].textContent}
                </p>
            </>)
    })

    return(
        <form className="mc cf" onChange={(event) => {
            props.setCorrect(null)
            props.setSelectedAnswer(event.target.getAttribute("index"))}
            }

            onClick={(event) => {
                props.setCorrect(null)
                props.setSelectedAnswer(event.target.getAttribute("index"))}
            }
            >
        {ChoicesJSX}
        </form>
    )
}

const Messages = ({props}) => {
    // console.log(props.selectedAnswer)
    const Message = (props.pressSubmit == 1 && props.selectedAnswer == -1)? "Please answer the above question." : ""

    return (<p className="messages">{Message}</p>)
}

const Question = (props) => {
    //console.log( props )
    // console.log( props.questionNum )
    if( props.questionNum != - 1){
        return(<>
            <section className="question cf">
                <p className="instruction"></p>
                <QuestionText props={props} />
                <ChoiceList props={props}/>
                <Messages props = {props}/>
            </section>

            <Buttons

                /* we don't have to assign every props again 
                like using props={...props, new element}
                I have tried and it did not work
                seem there is some syntax bug*/
                goToNextQuestion={props.goToNextQuestion}
                goToPrevQuestion={props.goToPrevQuestion}
                correctAnswer={props.choices.map( ( choice, _ ) => {
                    return( choice.getAttribute("fraction") == 1 )
                }).indexOf(true)}
                setCorrect={props.setCorrect}
                selectedAnswer={props.selectedAnswer}
                correct={props.correct}
                setSubmit={props.setSubmit}
                pressSubmit={props.pressSubmit}
                resetSelected={props.resetSelected}
                questionsList={props.questionsList}
            />
        </>)
    }else { return(<></>)}
}

export default Question
