import logo from './logo.svg'

import Header from './Header'
import Wrapper from './Wrapper'
import Question from './Question';
import Footer from './Footer';

import React from 'react';
import axios from 'axios'

import './App.css';
//path must be changed accordingly to the param query input

import XMLData from './xml/mc.xml'
//import './arrangeprotein.css'
//import './bootstrap.min.css'
//import './jquery.mobile-1.4.0.css'
//import './mcstyle.css'
//import './quiz.css'
//import './reset.css'
//import './css/style.css'

/*
PROBLEM:
  WHEN DO WE RESET QUESTION NUM => WHEN THE BUTTON IS PRESSED
  HOW DO WE SEND DATA FROM CHILD TO PARENTS IN REACT?

  SEND DATA FROM CHILD TO PARENT == SEND A FUNCTION FROM PARENT TO CHILD FOR A CHILD TO RUN AND RETURN 

  LOOK FOR FORM.JS in name-job

PROBLEM:
  SEEM TO BE ASYNCHRONOUS FUNCTION CALLING PROBLEM OCCURING
  STILL DO NOT KNOW HOW TO SOLVE IT...
*/

class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      isFetched: false,
      questionNum: 0,
      questionTexts: [],
      answers: []
    }
  }

  componentDidMount(){
    this.fetchQuestionsWithAxios()
  }

  fetchQuestionsWithAxios(){
    axios.get(XMLData,
      {"Content-Type":"application/xml; charset=utf-8"
    }).then((response)=>{

      console.log( "mounting APP")
      const parser = new DOMParser()
      var xml = parser.parseFromString(response.data,"text/xml")
      var questionTexts = Array.from(xml.getElementsByTagName("questiontext"))
      var answers = Array.from(xml.getElementsByTagName("answer"))

      this.setState({
          questionNum: 0,
          questionTexts: questionTexts,
          answers: answers,
          isFetched:true
      })
    }).catch( e => {
      console.log(e)
      this.setState({...this.state, isFetched: false })
    })
  }

  render(){
    //console.log( this.state )
    return (
      <div className="App">
        <Header/>
        <Wrapper/>
        <Question
          questionNum={this.state.questionNum}
          questionTexts={this.state.questionTexts}
          answers={this.state.answers}
          isFetched={this.state.isFetched}
        />
        <Footer />
      </div>
    )
  }
}

export default App;
