import React, { useState } from "react"
import Buttons from "./Buttons"

//contain question text
const QuestionText = ({props}) => {
    if( props.questionNum != -1 ){ //-1 is start menu
        return (<>
            <p className="question-number">Question {props.questionNum + 1} out of 5{props.Question}</p>
            <p className="question">{props.questionTexts[props.questionNum].textContent}</p>
        </>)

        /*
        return (props.isFetched) ? (
            <>
                <p className="question-number">Question {props.questionNum + 1} out of 5{props.Question}</p>
                <p className="question">{props.questionTexts[props.questionNum].textContent}</p>
            </>
        ) : (<p>Still Loading..</p>)  */

    } else { return <></> }
}


//display choices
const ChoiceList = ({props}) => {
    console.log(props)

    const ChoicesJSX = props.choices.map( (choice, index) => {
        return(
            <>
                <input
                    type="radio"
                    name={"answer-"+props.questionNum}
                    index={index}
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {props.selectedAnswer}{" "}
                {index}{" "}
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}
                    style={{display: ( props.selectedAnswer == index && props.correct !== null ) ? "display" : "none"}}>
                {/* HOW TO CHANGE STYLE FROM NONE TO BLOCK */}
                {choice.childNodes[3].textContent}
                </p>
            </>)
    })

    return(
        <form className="mc cf" onChange={(event) => {
            props.setSelectedAnswer(event.target.getAttribute("index"))}
            }>
        {ChoicesJSX}
        </form>
    )
}

const Choices = ({props}) => {
    //console.log(props)
    if( props.questionNum != -1 ){ //-1 is start menu
        return(
            <ChoiceList props={props}/>
            )
    }else return <></> 
}

const Question = (props) => {
    //console.log( props )

    return(<>
        <section className="question cf">
            <p className="instruction"></p>
            <QuestionText props={props} />
            <Choices props={props}/>
            <p className="messages"></p>
        </section>

        <Buttons
            goToNextQuestion={props.goToNextQuestion}
            goToPrevQuestion={props.goToPrevQuestion}
            correctAnswer={+props.choices.map( ( choice, index ) => {
                return( choice.getAttribute("fraction") == 1 )
            }).indexOf(true)}
            setCorrect={props.setCorrect}
            selectedAnswer={props.selectedAnswer}
          />
    </>)
}

export default Question
