import React from "react"


const Question = (props) => {

    const {answers, questionNum, questionTexts} = props
    const Choices = answers.filter( (_ , index) => {
        return ((questionNum*4 <= index) && (index <= questionNum*4 + 3))
    })

    const ChoiceDisplay = Choices.map( (choice, index) => {
        return(
            <div>
                <input
                    type="radio"
                    name=""
                    id={"answer-"+index}
                    value={choice.childNodes[1].textContent}/>
                <label
                    htmlFor={"answer-"+index}
                    className="hovereffect">
                {choice.childNodes[1].textContent}</label>
                <p
                    className="feedback"
                    id={"feedback-"+index}>
                </p>
            </div>
        )
    })

    return(
        <section className="question cf">
            <p className="instruction"></p>
            <p className="question-number">{questionNum+1}</p>
            <p className="question">{questionTexts[questionNum].textContent}</p> {/* THERE IS A PROBLEM AT THIS STAGE*/}
            <form className="mc cf">
                {ChoiceDisplay}
            </form>
            <p className="messages"></p>
        </section>
    )
}

export default Question
